    private void btnImparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnImparActionPerformed
        if (verificaValor()) {
            textArea.setText("");
            if (n1 > n2) {
                int i;
                for (i = n1; i >= n2; i--) {

                    if (i % 2 != 0) {

                        textArea.append("Numeros Impares " + i + "\n");

                    }

                }
            }

            // calculo de numeros pares
            int i;
            for (i = n1; i <= n2; i++) {

                if (i % 2 != 0) {

                    textArea.append("Numeros Impares " + i + "\n");
                }
                }
            }
    }//GEN-LAST:event_btnImparActionPerformed

    private void btnParesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnParesActionPerformed

        // conversao de String para Int
        if (verificaValor()) {
            textArea.setText("");
            if (n1 > n2) {

                // calculo de numeros pares
                for (int i = n1; i >= n2; i--) {

                    if (i % 2 == 0) {

                        textArea.append("Numeros pares " + i + "\n");

                    }
                }

            } else {

                // calculo de numeros pares
                for (int i = n1; i <= n2; i++) {

                    if (i % 2 == 0) {

                        textArea.append("Numeros pares " + i + "\n");

                    }
                }

            }

    }//GEN-LAST:event_btnParesActionPerformed
    }
