 private boolean verificaValor() {

        
        
        try {

            n1 = Integer.parseInt(this.text1.getText());
            n2 = Integer.parseInt(this.text2.getText());
            return true;

        } catch (NumberFormatException e) {

            if (text1.getText().isEmpty() || text1.getText().matches("[a-z]")) {
                txtErro.setForeground(Color.red);
                txtErro.setText("Invalido, digite um valor inicial");
                text1.setText("");
                text1.requestFocus();

            } else if (text2.getText().isEmpty() || text2.getText().matches("[a-z]")) {
                txtErro.setForeground(Color.RED);
                txtErro.setText("Invalido, digite um valor final");
                text2.setText("");
                text2.requestFocus();
            }

        }

        return false;
    }

    void limpar() {

        this.textArea.setText("");
        this.text1.setText("");
        this.text2.setText("");
        this.txtErro.setText("");

    }


